function AmigaDecrunch(howlong){
var that = this;

var mycanvas;

var amigadiskH=1;
var amigadiskY=332;
var isfinished=-4;
var amigaWait=150;
var waitbeforedecrunch=100;
var base64imagesready=0;

function get_color() {
	var choices = ['#000088','#005544','#0033FF','#0022EE','#002200','#00DD44','#009966','#0099DD','#00BBCC','#00FF00'];
	var color = choices[Math.round(Math.random() * 10)];
             	return color;
}

function inc_cur() {
	base64imagesready++;	
	console.log('base64 image '+base64imagesready+' cached.');

	// All images are loaded, get dimensions:
	if (base64imagesready>=3) {
        w1 = that.amigashell.width;
        h1 = that.amigashell.height;
        w2 = that.amigashelltop.width;
        h2 = that.amigashelltop.height;
        w3 = that.amigadisk.width;
        h3 = that.amigadisk.height;
	}

}

   // Amiga shell window
    this.amigashell = new Image() ;
this.amigashell.onload = function(){ inc_cur(); };
    // base64 encoded PNG
    this.amigashell.src="data:image/png;base64,";

// top part of shell
this.amigashelltop =new Image() ;
this.amigashelltop.onload = function(){ inc_cur(); };
// base64 encoded PNG
this.amigashelltop.src="data:image/png;base64,";

// floppy disk
this.amigadisk=new Image();
this.amigadisk.onload = function(){ inc_cur(); };
//base64 encoded PNG
this.amigadisk.src="data:image/png;base64,";

tempcanvas=new canvas(720,568);

var W=720;
var H=568;

        var w1 = null;
        var h1 = null;
        var w2 = null;
        var h2 = null;
        var w3 = null;
        var h3 = null;

  this.doDecrunch=function(dest) {

tempcanvas.fill('#441144');

	if(isfinished==-4){
		tempcanvas.fill('#000000');
		amigaWait--;
		if ((amigaWait<=0) && (base64imagesready==3)) isfinished=-3;
		}

	if(isfinished==-3){
		tempcanvas.contex.drawImage(this.amigashell,0,0,w1,h1);
		tempcanvas.contex.drawImage(this.amigadisk,498,amigadiskY,w3,h3);
		tempcanvas.contex.drawImage(this.amigashelltop,0,0,w2,h2);
	
		waitbeforedecrunch--;
		if (waitbeforedecrunch<=0){
			waitbeforedecrunch=100;
			isfinished=-2;
			}
		}

	if(isfinished==-2){
		tempcanvas.contex.drawImage(this.amigashell,0,0,w1,h1);
		tempcanvas.contex.drawImage(this.amigadisk,498,amigadiskY,w3,h3*amigadiskH);
		tempcanvas.contex.drawImage(this.amigashelltop,0,0,w2,h2);
	
		amigadiskH-=0.01;
		if (amigadiskH<=0.5) amigadiskH=0.5;
		amigadiskY--;
		if (amigadiskY<=132) isfinished=-1;
		}

	if (isfinished==-1){
		waitbeforedecrunch--;
		if (waitbeforedecrunch<=0){
			waitbeforedecrunch=100;
			isfinished=0;
			}
		}

	if (isfinished==0){
		var y=0;
		while(y<568){
			var barh=(1+Math.random())*8 ;
			var extrah=Math.floor((Math.random()*5)+1);
			var mycolor;
			mycolor = get_color();
			tempcanvas.contex.fillStyle = mycolor ;
			tempcanvas.contex.fillRect(0,y,720,barh+extrah) ;
			y += barh;
			}
		howlong--;
		if (howlong<=0) isfinished=1;
		if (isfinished==1) this.finished=1;
		}

        dest.contex.ImageSmoothingEnabled=false;
        dest.contex.mozImageSmoothingEnabled=false;
        dest.contex.oImageSmoothingEnabled=false;

dest.fill('#AAAAAA');

 var scx = dest.contex.canvas.width/W  ;
        var scy = dest.contex.canvas.height/H  ;

        // draw that decrunch
        tempcanvas.draw(dest,0,0,1,0,scx,scy);
}

return this ;
}
 